// n8n Code node (JavaScript) â€” Split binary_objects then merge into one item

if (!items || items.length === 0) {
  return [];
}

const originalJson = items[0].json ?? {};
const binaryList = Array.isArray(originalJson?.body?.binary_objects)
  ? originalJson.body.binary_objects
  : [];

/** Remove any data:...;base64, prefix from a base64 string */
function stripDataUrlPrefix(value) {
  return String(value ?? '').replace(/^data:.*?;base64,/, '');
}

/** Cross-platform basename */
function getBaseName(pathValue) {
  const s = String(pathValue ?? '');
  const slash = s.split('/').pop();
  const backslash = s.split('\\').pop();
  return (slash && slash.length <= s.length ? slash : backslash) || '';
}

/** Sanitize a key; fallback to data<index> if empty */
function makeSafeKey(name, index) {
  const cleaned = String(name ?? '')
    .trim()
    .toLowerCase()
    .replace(/[^a-z0-9_\-]/g, '_');
  return cleaned || `data${index}`;
}

const mergedBinary = {};

// If there are binaries, collect them all into a single `binary` object
if (binaryList.length > 0) {
  for (let i = 0; i < binaryList.length; i++) {
    const binaryObject = binaryList[i];

    const desiredKey = makeSafeKey(binaryObject?.name, i);
    let finalKey = desiredKey;
    if (mergedBinary[finalKey]) {
      finalKey = `${finalKey}__${i}`; // avoid overwriting on collision
    }

    mergedBinary[finalKey] = {
      data: stripDataUrlPrefix(binaryObject?.data),
      fileName: getBaseName(binaryObject?.path) || desiredKey,
      mimeType: binaryObject?.mime_type || 'application/octet-stream',
    };
  }
}

// Match the merger node: keep first JSON and add _sources reflecting the (virtual) split count
const outputJson = {
  ...(originalJson ?? {}),
  _sources:
    binaryList.length > 0
      ? Array.from({ length: binaryList.length }, (_, i) => ({ index: i, ...(originalJson ?? {}) }))
      : [{ index: 0, ...(originalJson ?? {}) }],
};

// If no binaries existed, keep an empty `binary` object (merger node behavior)
return [
  {
    json: outputJson,
    binary: mergedBinary,
  },
];
